import React, { useEffect } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { getScores, updateScores } from './firebase';
import ScoreCard from './components/ScoreCard'; 
import _ from 'lodash';

function End() {
    const history = useHistory();
    let [prevScores, setPrevScoress] = use



    useEffect(() => {
        const getData = async () => {
            const elapsedTime = history.location.state.time;
            const playerName = history.location.state.name;
            const timeDisplay = msToTime(elapsedTime);
            const playerScore = { name: playerName, time: timeDisplay }
            const prevScores = await getScores();
            const curScores = updateScores(playerScore, prevScores);
        }
        getData();
    }, [])
    const highScoreMessage = (_.isEqual(prevScores, curScores)) ? null :
        <h3>Congratulations! Your time was quick enough to make the high score board!</h3>
    return (
        <div>
            <h2>You found all the Pok√©mon in {timeDisplay.slice(0, 2)} minutes and {timeDisplay.slice(3)} seconds!</h2>
            {highScoreMessage}
            <h3>High Scores: </h3>
            {curScores.map(scoreCard => <ScoreCard pName={scoreCard.name} pScore={scoreCard.score}></ScoreCard>)}
            <Link to={{
                pathname: '/'
                }}>Click to play again</Link>
        </div>
    )
}

function msToTime(s) {
    const ms = s % 1000;
    s = (s - ms) / 1000;
    const secs = s % 60;
    s = (s - secs) / 60;
    const mins = s % 60;
    return (mins + ':' + secs + '.' + ms);
}

export default End;
