import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import pokePic from './assets/pokemon_wallpaper.jpeg';
import { selectMons, getPokePics, checkFound } from './firebase';
import PokeCard from './components/PokeCard';
import PokeView from './components/PokeView';
import PokeSelect from './components/PokeSelect';

function Game() {
    let [foundPokes, setFoundPokes] = useState([]);
    let [currentBoxCords, setCurrentBoxCords] = useState(null);
    let [pickedPokemon, setPickedPokemon] = useState([]);
    let [pokePics, setPokePics] = useState([]);
    let [pokeCards, setPokeCards] = useState(null);
    let [boxView, setBoxView] = useState(null);

    useEffect(() => {
        const getData = async () => {
            const mons = await selectMons();
            const pics = await getPokePics(mons);
            const foundArray = [    
                {
                    id: mons[0].id,
                    found: false
                },
                {
                    id: mons[1].id,
                    found: false
                },
                {
                    id: mons[2].id,
                    found: false 
                },
                {
                    id: mons[3].id,
                    found: false  
                },
                {
                    id: mons[4].id,
                    found: false
                }
            ];
            setPickedPokemon(mons);
            setPokePics(pics);
            setFoundPokes(foundArray);
            setPokeCards(pics.map((mon, index) => 
                <PokeCard
                    pokePic={mon.src}
                    pokeName={mons[index].name}
                    found={foundArray[index].found}
                    key={index}
                ></PokeCard>));
        }
        getData();
    }, [])

    const history = useHistory();
    const startTime = Date.now();
    const name = history.location.state.name;

    const selectArea = (e) => {
        const xClick = e.clientX;
        const yClick = e.clientY;
        const boxCords = [xClick - 45, xClick + 45, yClick - 55, yClick + 55];
        setCurrentBoxCords([boxCords[0], boxCords[2]]);
    }

    const updateFound = (pokeID) => {
        const found = checkFound(pokeID);
        const index = foundPokes.map((poke) => poke.id).indexOf(pokeID);
        setFoundPokes(foundPokes.slice(0, index).concat({id: pickedPokemon[index].id, found: found}).concat(foundPokes.slice(index + 1)));
        setCurrentBoxCords(null);
    }

    useEffect(() => {
        const totalFound = foundPokes.reduce((prev, cur) => prev + cur.found, 0);
        if (totalFound === 5) {
            const elapsedTime = Date.now() - startTime;
            history.push({
                pathname: '/end',
                state: {
                    time: elapsedTime,
                    name: name
                }
            });
        }
    })

    return (
        <div>
            <h2>Pokémon to find:</h2>
            <div className='pokeBox'>
                {pokeCards}
            </div>
            <img src={pokePic} alt='pokemon-game' onClick={selectArea}></img>
            {currentBoxCords ? 
                <PokeView boxCords={currentBoxCords}>
                    <PokeSelect 
                        pokeList={pickedPokemon} 
                        foundPokes={foundPokes} 
                        foundFunction={updateFound}>
                    </PokeSelect>
                </PokeView> 
                : null}
        </div>
    );
}

export default Game;
